import { useNavigate, Form, useNavigation, data } from "react-router-dom";

import classes from "./EventForm.module.css";

function EventForm({ method, event }) {
  const navigate = useNavigate();
  const navigation = useNavigation();

  const isSubmitting = navigation.state === "submitting";

  function cancelHandler() {
    navigate("..");
  }

  return (
    <Form method="POST" className={classes.form}>
      {data?.errors && (
        <ul>
          {Object.values(data.errors).map((err) => (
            <li key={err}>{err}</li>
          ))}
        </ul>
      )}
      <p>
        <label htmlFor="title">Title</label>
        <input
          defaultValue={event?.title}
          id="title"
          type="text"
          name="title"
        />
      </p>
      <p>
        <label htmlFor="image">Image</label>
        <input defaultValue={event?.image} id="image" type="url" name="image" />
      </p>
      <p>
        <label htmlFor="date">Date</label>
        <input defaultValue={event?.date} id="date" type="date" name="date" />
      </p>
      <p>
        <label htmlFor="description">Description</label>
        <textarea
          defaultValue={event?.description}
          id="description"
          name="description"
          rows="5"
        />
      </p>
      <div className={classes.actions}>
        <button disabled={isSubmitting} type="button" onClick={cancelHandler}>
          Cancel
        </button>
        <button disabled={isSubmitting}>
          {isSubmitting ? "Submitting" : "Save"}
        </button>
      </div>
    </Form>
  );
}

export default EventForm;
